import React, { Component } from 'react';


export default class HotelForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hotel_name: "",
      city: "",
      state: "",
      zip_code: "",
      biography: "",

    }
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value })
  }

  render() {
    return (
      <div className="hotelForm">
      <form  onSubmit={(e) => {
        e.preventDefault();
        this.props.createHotel(this.state)
      }}>
        <label htmlFor="hotel_name">Hotel</label>
        <input
          type="text"
          name="hotel_name"
          value={this.state.hotel_name}
          onChange={this.handleChange}
        />
        <label htmlFor="city">City</label>
        <input
          type="text"
          name="city"
          value={this.state.city}
          onChange={this.handleChange}
        />
        <label htmlFor="state">setState</label>
        <input
          type="text"
          name="State"
          value={this.state.state}
          onChange={this.handleChange}
        />
        <label htmlFor="zip_code">Zip-code</label>
        <input
          type="text"
          name="zip_code"
          value={this.state.zip_code}
          onChange={this.handleChange}
        />
        <button>Select</button>
      </form>
      </div>
    )
  }
}
