import React, { Component } from 'react';
import ReservationForm from '../components/'

export default class HotelForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hotels :[{
      hotel_name: "",
      city: "",
      state: "",
      zip_code: "",
      biography: "",
      imagelink: ""
    }]
  }
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value })
  }

  render() {
    return (
      <div >
      <form className="hotelForm"  onSubmit={(e) => {
        e.preventDefault();
        this.props.createHotel(this.state)
      }}>
        <label htmlFor="hotel_name">Hotel</label>
        <input
          type="text"
          name="hotel_name"
          value={this.state.hotel_name}
          onChange={this.handleChange}
        />
        <label htmlFor="city">City</label>
        <input
          type="text"
          name="city"
          value={this.state.city}
          onChange={this.handleChange}
        />
        <label htmlFor="state">State</label>
        <input
          type="text"
          name="state"
          value={this.state.state}
          onChange={this.handleChange}
        />
        <label htmlFor="zip_code">Zip-code</label>
        <input
          type="text"
          name="zip_code"
          value={this.state.zip_code}
          onChange={this.handleChange}
        />
        <label htmlFor="biography">Biography</label>
        <input
          type="text"
          name="biography"
          value={this.state.biography}
          onChange={this.handleChange}
        />
        <button>Submit</button>
      </form>
      </div>
    )
  }
}
