import React, { Component } from 'react';
import { Link } from 'react-router-dom';


export default class CreateHotelList extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    console.log(this.props.hotels)
    return (
      <div>
        <Link to="/hotels/new">Add a Hotel To Your List</Link>
      <div id="hotel-display">

        {this.props.hotels && this.props.hotels.map(hotel => (
          <div key={hotel.id} className="hotelContainer">
            <Link to={`/hotels/${hotel.id}`}>
              {hotel.hotel_name}
              </Link>
            <Route path="/hotels/reservations" render={() => (<ReservationContainer />)} />
            {hotel.imagelink}
            <p>{hotel.biography}</p>
            <p>{hotel.city}, {hotel.state}, {hotel.zip_code}</p>

          </div>
        ))}
      </div>
      </div>
    )
  }
}
