import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import ReservationForm from './ReservationForm';


export default class CreateReservationlList extends Component { 
  constructor(props){
    super(props);

  }

  render(){
    // console.log(this.props.reservations)
  return (
    <div>
      <ReservationForm 
      createReservation={this.createReservation} />
      <Link to={`/hotels/${this.props.currentHotel.id}/reservations/new`}>Make a new Reservation</Link>
      <div id="reservation-display">
      {this.props.reservations && this.props.reservations.map(reservation => (
        <div key={reservation.id}>
           {/* <Link to={`/hotels/${hotel.id}/reservations/${reservation.id}`}> */}
           {reservation.reservation_desc}
              {/* </Link> */}
        </div>
      ))}
    </div>
    </div>
  )
}
